import "./Die.css";

class Die extends Component {

  static defaultProps = {
    numberWords: ['one', 'two', 'three', 'four', 'five', 'six'],
  };

constructor(props){
  super(props);
  this.handleClick = this.handleClick.bind(this);
}


handleClick(){
  this.props.handleClick(this.props.idx);
